testvar = "@"                       # Variable `testvar` wird mit dem Wert "@" (ein At-Zeichen) initialisiert.
testwert = "webscripting@gmx.de"    # Variable `testwert` wird mit einer E-Mail-Adresse initialisiert.
print(testvar in testwert)          # Überprüft, ob "@" in der Zeichenkette `testwert` enthalten ist, und gibt True oder False aus.
print(testvar not in testwert)      # Überprüft, ob "@" nicht in der Zeichenkette `testwert` enthalten ist, und gibt True oder False aus.