print(8 != 4 + 4)                # Prüft, ob 8 ungleich 4 + 4 (also 8) ist. Ergebnis: False, da 8 == 8.
print(7 != 4 + 4)                # Prüft, ob 7 ungleich 4 + 4 (also 8) ist. Ergebnis: True, da 7 != 8.
print(5 < 6)                     # Prüft, ob 5 kleiner als 6 ist. Ergebnis: True, da 5 < 6.
print(6 <= 6)                    # Prüft, ob 6 kleiner oder gleich 6 ist. Ergebnis: True, da 6 == 6.
print(4 == 5)                    # Prüft, ob 4 gleich 5 ist. Ergebnis: False, da 4 != 5.
print("4" == 4)                  # Prüft, ob der String "4" gleich der Zahl 4 ist. Ergebnis: False, unterschiedliche Datentypen.
print(4 == 2 + 2)                # Prüft, ob 4 gleich 2 + 2 (also 4) ist. Ergebnis: True, da 4 == 4.
print(5 > 6)                     # Prüft, ob 5 größer als 6 ist. Ergebnis: False, da 5 < 6.
print(5 >= 5)                    # Prüft, ob 5 größer oder gleich 5 ist. Ergebnis: True, da 5 == 5.
print(6 >= 5)                    # Prüft, ob 6 größer oder gleich 5 ist. Ergebnis: True, da 6 > 5.
print(4 >= 5)                    # Prüft, ob 4 größer oder gleich 5 ist. Ergebnis: False, da 4 < 5.